import { useContext } from 'react';
import Head from 'next/head';
import Header from './Header';
import DataContext from './DataContext';
import Marquee from './Marquee';
import './Layout.scss';

export default function Layout({children, pageTitle}) {
  const { pages } = useContext(DataContext);
  console.log('pages..', pages)
  if (!pages) {
    return (
      <>
      <Head>
        <title>Marquee Test</title>
        <meta
          name="viewport"
          content="initial-scale=1.0, width=device-width"
          charSet="utf-8"
          key="viewport"
        />
      </Head>
      {/* add background styling.... */}
      <div className="layout">
        <Header />
      </div>
    </>
    )
  }
  let pageData;
  for (let data of pages) {
    if (data.title === pageTitle) {
      pageData = data;
      break;
    }
  }
  console.log('data...', pageData)
  const { blocks } = pageData;
  const content = blocks[0];
  let { headline, subhead, cta, background } = content;

  if (!background) background = 'slide-one.jpg'
  return (
    <>
      <Head>
        <title>Marquee Test</title>
        <meta
          name="viewport"
          content="initial-scale=1.0, width=device-width"
          charSet="utf-8"
          key="viewport"
        />
      </Head>
      {/* add background styling.... */}
      <div className="layout" style={{backgroundImage: `url(/${background})`}}>
        <Header />
        <Marquee 
          headline={headline} 
          subhead={subhead} 
          cta={cta} 
        />
      </div>
    </>
  )
}
